<template>
  <a-modal
    :open="modal"
    title="我的反馈"
    @cancel="modal = false"
    :bodyStyle="{
      padding: '29px 0',
      display: 'flex',
      justifyContent: 'center',
      alignItem: 'center'
    }"
  >
    <a-spin v-if="loading"/>
    <div class="scrollList" v-else>
      <div v-for="item in commentList">
        <div class="comment">
          <div class="avatar">
            <a-avatar :size="40" :src="item.avatar" />
          </div>
          <div class="detail">
            <span class="word">{{ item.remark }}</span>
            <div class="images">
              <a-image-preview-group>
                <a-image
                  v-for="src in item.feedbackFileIds"
                  :key="src"
                  :width="56"
                  :height="56"
                  placeholder
                  :src="src"
                  :fallback="fallback"
                />
              </a-image-preview-group>
            </div>
            <div class="other">
              <span class="grayWord">{{ item.createTime }}</span>
              <span class="expand" v-if="item.handleOpinion" @click="item.ifExpand = !item.ifExpand">{{
                item.ifExpand ? "收起" : "展开回复"
              }}</span>
            </div>

            <div class="reply" v-show="item.ifExpand && item.handleOpinion">
              <img src="@/assets/icons/logo@3x.png" class="reply_avatar" />
              <div class="reply_detail">
                <span class="grayWord">平台答复：</span>
                <span class="reply_word">{{ item.handleOpinion }}</span>
                <span class="grayWord time">{{ item.handleTime }}</span>
              </div>
            </div>
          </div>
        </div>
        <a-divider style="margin: 24px 0" />
      </div>
      <a-empty v-if="commentList.length === 0"/>
    </div>
    <template #footer>
      <a-button type="primary" @click="modal = false">返回</a-button>
    </template>
  </a-modal>
</template>

<script setup lang="ts">
import { ref, watch } from "vue";
const modal = defineModel("open", { type: Boolean, default: false });

const fallback =
  "data:image/png;base64,";

import { myfeedback, resFeedback } from "api/feedback";
type commentList = resFeedback & {
  avatar: string;
  ifExpand: boolean;
}
const commentList = ref<Array<commentList>>([]);
const loading = ref(false);
watch(
  () => modal.value,
  async (val) => {
    if (val) {
      loading.value = true;
      const res = await myfeedback();
      loading.value = false;
      commentList.value = res.data.map(item => {
        return {
          ...item,
          avatar: "",
          ifExpand: true,
        }
      });
    }
  },
);

</script>

<style lang="scss" scoped>
@import "@/assets/styles/_handle.scss";
.grayWord {
  @include font_color("word-gray");
}
.scrollList {
  padding: 0 16px;
  max-height: 500px;
  overflow-y: scroll;
  overflow-x: hidden;
}
.comment {
  display: flex;
  gap: 8px;
  .avatar {
    width: 40px;
    height: 40px;
  }
  .detail {
    display: flex;
    flex-direction: column;
    gap: 8px;
    .word {
      font-size: 14px;
    }
    .images {
      display: flex;
      flex-wrap: wrap;
      gap: 8px;
    }
    .other {
      display: flex;
      gap: 8px;
      .expand {
        @include font_color("primary");
        cursor: pointer;
        -moz-user-select: none;
        -o-user-select: none;
        -khtml-user-select: none;
        -webkit-user-select: none;
        -ms-user-select: none;
        user-select: none;
      }
    }
    .reply {
      display: flex;
      gap: 8px;
      margin-top: 18px;
      &_avatar {
        width: 24px;
        height: 24px;
        border-radius: 50%;
      }
      &_detail {
        display: flex;
        flex-direction: column;
        .time {
          margin-top: 8px;
        }
      }
    }
  }
}
</style>
